template.value:
resp.setContentType("text/html;charset=UTF-8");
try(PrintWriter writer = resp.getWriter()){
writer.println("<!DOCTYPE html>");
writer.println("<html>");
writer.println("  <head>");
writer.println("      <meta charset=\"UTF-8\">");
writer.println("      <title>$title$</title>");
writer.println("  </head>");
writer.println("  <body>");
writer.println("      <h1>$title$!</h1>");
writer.println("  </body>");
writer.println("</html>");
}
